import React, { useEffect, useState, Fragment } from "react";


function useCountdown(targetDate) {
    debugger
    const countDownDate = new Date(targetDate).getTime();

    const [countDown, setCountDown] = useState(countDownDate - new Date().getTime());

    useEffect(() => {
        const interval = setInterval(() => {
            setCountDown(countDownDate - new Date().getTime());
        }, 1000);

        return () => clearInterval(interval);
    }, [countDownDate]);

    // calculate time left
    const days = Math.floor(countDown / (1000 * 60 * 60 * 24));
    const hours = Math.floor(
        (countDown % (1000 * 60 * 60 * 24)) / (1000 * 60 * 60)
    );
    const minutes = Math.floor((countDown % (1000 * 60 * 60)) / (1000 * 60));
    const seconds = Math.floor((countDown % (1000 * 60)) / 1000);

    if (days + hours + minutes + seconds <= 0) {
        return <div className="expired-notice">
            <span>Expired!!!</span>
            {/* <p>Please select a future date and time.</p> */}
        </div>

    } else {
        return <div className="show-counter">
            <span>{days} : {hours} : {minutes} : {seconds}</span>
        </div>

    }
};

const CountdownTimer = () => {

    const dataList = [
        {
            id: '1',
            date: '2022-12-07 15:00:00',
        }, {
            id: '2',
            date: '2022-12-07 15:10:00',
        }, {
            id: '3',
            date: '2022-12-07 15:20:00',
        }, {
            id: '3',
            date: '2022-12-07 15:30:00',
        }, {
            id: '4',
            date: '2022-12-07 15:40:00',
        }, {
            id: '5',
            date: '2022-12-07 15:50:00',
        }, {
            id: '6',
            date: '2022-12-07 15:60:00',
        },

    ];

    return <div>
        {dataList.map(data =>
            <div className="tr">
                <div>{data.id}</div>

                <useCountdown targetDate={new Date(data.date)} />
            </div>

        )}
    </div >

}

export default CountdownTimer
