import React, { Component } from 'react'
import { connect } from 'react-redux'

import { RemoveItemAction, UpdateItemStatusAction, UpdateItemAction } from '../Redux/Actions'
class TableComponent extends Component {
    constructor({ OnEditItem, ItemsList, OnRemoveItemAction, OnUpdateItemStatusAction }) {
        super()
        this.state = {
            ItemsList: ItemsList,
        }
    }

    OnStatusUpdate(Item) {
        this.props.OnUpdateItemStatusAction(Item)
    }

    OnRemove(Item) {
        this.props.OnRemoveItemAction(Item)
    }

    render() {
        return (
            <div>
                <table className="table">
                    <thead>
                        <tr>
                            <th scope="col">#</th>
                            <th scope="col">Id</th>
                            <th scope="col">Name</th>
                            <th scope="col">Date &amp; Time</th>
                            <th scope="col">Status</th>
                            <th scope="col">Action</th>
                        </tr>
                    </thead>
                    <tbody>
                        {this.props.ItemsList ? this.props.ItemsList.map(Item =>

                            <tr key={Item.id}>
                                <td>
                                    <input className="form-check-input" type="checkbox" checked={!Item.status && 'checked'} onChange={() => { this.OnStatusUpdate(Item) }} />
                                </td>
                                <td>{Item.id}</td>
                                <th className="1" scope="row" style={{ textDecoration: !Item.status && 'line-through' }}>{Item.name}
                                </th>

                                <td>{Item.time}</td>
                                <td>{Item.status ? 'Active' : 'InActive'}</td>
                                <td>
                                    <div className="btn-group btn-group-sm" role="group" >
                                        <button className="btn btn-secondary" onClick={() => this.props.OnEditItem(Item)}> Edit</button>
                                        <button className="btn btn-danger" onClick={() => this.OnRemove(Item)}> Remove</button>
                                    </div>
                                </td>
                            </tr>
                        ) : ''}
                    </tbody>
                </table>


            </div>
        )
    }
}

const MapStateToProps = (Store) => {
    return {
        ItemsList: Store.FilteredItemList
    }
}

const MapDispatchToProps = (Dispatch) => {
    return {
        OnRemoveItemAction: (Details) => Dispatch(RemoveItemAction(Details)),
        OnUpdateItemStatusAction: (Details) => Dispatch(UpdateItemStatusAction(Details)),
        OnUpdateItemAction: (Details) => Dispatch(UpdateItemAction(Details)),
    }
}
export default connect(MapStateToProps, MapDispatchToProps)(TableComponent);
