// import './App.css';
import { connect } from 'react-redux';
import React from 'react';
import TableComponent from './Components/TableComponent';
import { AddItemAction, UpdateItemAction, UpdateFilterAction, UpdateAction } from './Redux/Actions'
import { SORT, ACTIONOPTIONS, SHOWSELECT } from './Redux/Constants'

class App extends React.Component {

  constructor({ OnAddItemAction, OnUpdateItemAction, ItemsListLength, OnUpdateFilterAction, OnUpdateAction }) {
    super();
    this.state = {
      ShowForm: false,
      InputValue: '',
      EditDetails: {},
    }
  }

  onFilterChange(event) {
    this.props.OnUpdateFilterAction({ [event.target.name]: event.target.value })
  }

  updateInputValue = (event) => {

    this.setState({ ...this.state, InputValue: event.target.value })
    this.onFilterChange(event)
  }

  SubmitForm() {
    if (!this.state.InputValue.length)
      return
    if (this.state.EditDetails.id) {
      this.props.OnUpdateItemAction({ Updatevalue: this.state.InputValue, EditDetails: this.state.EditDetails })
    } else {
      this.props.OnAddItemAction({ NewValue: this.state.InputValue })
    }
    this.VisibleForm(false)
  }

  VisibleForm(Visible = true) {
    this.setState({ ...this.state, ShowForm: Visible, InputValue: '' })
  }

  OnEditItem = (Item) => {
    this.setState({ ...this.state, InputValue: Item.name, EditDetails: Item, ShowForm: true })
  }

  actionChange = (event) => {
    this.props.OnUpdateAction({ [event.target.name] : event.target.value })

  }
  render() {
    return <div className="App">
      <header className="App-header container card">
        <header className="card-header">
          <h3>Things To Do</h3>
        </header>

        <div className="card-body">
          {this.state.ShowForm &&
            <div >

              <input type="text" className="form-control" name='SearchedText' placeholder="Enter Name" value={this.state.InputValue} onChange={this.updateInputValue} style={{ width: '50%' }} />
              <div className="btn-group btn-group-sm" >
                <button className="btn btn-light " onClick={() => this.VisibleForm(false)} >X</button>
                <button className="btn btn-light " onClick={() => this.SubmitForm()} > {this.state.EditDetails.id ? 'Update' : 'Add'}</button>
              </div>
            </div>
          }
          <TableComponent OnEditItem={this.OnEditItem} />
        </div>

        <div className="card-footer">
          <div className="row">
            <div className="col-2">
              <div className="btn-group btn-group-sm" role="group" onClick={() => this.VisibleForm()}>
                <button className="btn btn-secondary"> Search</button>
                <button className="btn btn-primary"> Add</button>
              </div>
            </div>

            <select id="btnaction" className="col-2" name='Action' onChange={this.actionChange}>
              <option disabled selected >Action</option>
              <option value={ACTIONOPTIONS.SELECTALL}>Select All</option>
              <option value={ACTIONOPTIONS.DELETESELECTED}>Delete Selected</option>
              <option value={ACTIONOPTIONS.DELETEALL}>Delete All</option>
            </select>

            <div className="col-2">Total Records : <span id="totalSpan">{this.props.ItemsListLength}</span></div>

            <select name='Sort' className="col-2" onChange={(e) => this.onFilterChange(e)}>
              <option disabled selected>Sort</option>
              <option value={SORT.AZ}>Name {SORT.AZ}</option>
              <option value={SORT.ZA}>Name {SORT.ZA}</option>
              <option value={SORT.Date_Ascending}>{SORT.Date_Ascending}</option>
              <option value={SORT.Date_Descending}>{SORT.Date_Descending}</option>
            </select>


            <select name='Active' className="col-3" onChange={(e) => this.onFilterChange(e)}>
              <option disabled selected>Show</option>
              <option value={SHOWSELECT.ALL}>All</option>
              <option value={SHOWSELECT.ACTIVE}>Active </option>
              <option value={SHOWSELECT.INACTIVE} > InActive </option>
            </select>


          </div>
        </div >
      </header >
    </div >
  };
}

const MapStateToProps = (Store) => {
  return {
    ItemsListLength: Store.FilteredItemList.length
  }
}

const MapDispatchToProps = (Dispatch) => {
  return {
    OnAddItemAction: (Details) => Dispatch(AddItemAction(Details)),
    OnUpdateItemAction: (Details) => Dispatch(UpdateItemAction(Details)),
    OnUpdateFilterAction: (Details) => Dispatch(UpdateFilterAction(Details)),
    OnUpdateAction: (Details) => Dispatch(UpdateAction(Details)),
  }
}

export default connect(MapStateToProps, MapDispatchToProps)(App);
