import { ADD_ACTION, UPDATE_ACTION, REMOVE_ACTION, STATUS_UPDATE_ACTION, FILTER_UPDATE_ACTION, ITEMLIST_UPDATE_ACTION } from './Constants'

const AddItemAction = (details) => {
    return {
        type: ADD_ACTION,
        AddDetails: details
    }
}
const UpdateItemAction = (details) => {
    return {
        type: UPDATE_ACTION,
        UpdateDetails: details
    }
}
const RemoveItemAction = (details) => {
    return {
        type: REMOVE_ACTION,
        RemoveDetails: details
    }
}
const UpdateItemStatusAction = (details) => {
    return {
        type: STATUS_UPDATE_ACTION,
        UpdateDetails: details
    }
}

const UpdateAction = (details) => {
    return {
        type: ITEMLIST_UPDATE_ACTION,
        UpdateActionDetails: details
    }
}

const UpdateFilterAction = (details) => {
    return {
        type: FILTER_UPDATE_ACTION,
        UpdateFilterDetails: details
    }
}


export { AddItemAction, UpdateItemAction, RemoveItemAction, UpdateItemStatusAction, UpdateFilterAction, UpdateAction }
