import React, { useEffect, useState, Fragment } from "react";
import { useCountdown } from "./useCountdown";

function setCountdown(targetDate, days, hours, minutes, seconds) {

    const countDownDate = new Date(targetDate).getTime();

    const [countDown, setCountDown] = useState(
        countDownDate - new Date().getTime()
    );

    useEffect(() => {
        const interval = setInterval(() => {    
            setCountDown(countDownDate - new Date().getTime());
        }, 1000);

        return () => clearInterval(interval);
    }, [countDownDate]);

    if (days + hours + minutes + seconds <= 0) {
        return <>
            <div className="expired-notice">
                <span>Expired!!!</span>
                <p>Please select a future date and time.</p>
            </div>
        </>
    } else {
        return <>
            <div className="show-counter">
                <span>{days} : {hours} : {minutes} : {seconds}</span>
            </div>
        </>
    }
}

const CountdownTimer = () => {

    const dataList = [
        {
            id: '1',
            date: '2022-12-07 15:00:00',
        }, {
            id: '1',
            date: '2022-12-07 15:15:00',
        }, {
            id: '1',
            date: '2022-12-07 15:30:00',
        }, {
            id: '1',
            date: '2022-12-07 15:45:00',
        },

    ];

    return <div>
        {dataList.map(data => {
            <>
                <div>{data.id}</div>
                <div>{setCountdown(useCountdown(new Date(data.date)))}</div>
            </>
        }
        )}
    </div>

}

export default CountdownTimer
